        onlyAttestation(_view)
        returns (bytes29)
    {
        (uint8 guardSigs, uint8 notarySigs) = _agentSignatures(_view);
        require(_notaryIndex < notarySigs, "Out of range");
        return
            _view.slice({
                _index: OFFSET_FIRST_SIGNATURE +
