    function nibbleHex(uint8 _byte) internal pure returns (uint8 _char) {
        uint8 _nibble = _byte & 0x0f; // keep bottom 4 bits, zero out top 4 bits
        _char = uint8(NIBBLE_LOOKUP[_nibble]);
    }

    /**
