    function argumentsPayload(bytes29 _view)
        internal
        pure
        onlyType(_view, SynapseTypes.CALL_PAYLOAD)
        returns (bytes29)
    {
        return _view.sliceFrom({ _index: OFFSET_ARGUMENTS, newType: SynapseTypes.RAW_BYTES });
    }
