            uint256(summitTip_) << SHIFT_SUMMIT_TIP |
            uint256(attestationTip_) << SHIFT_ATTESTATION_TIP |
            uint256(executionTip_) << SHIFT_EXECUTION_TIP |
            uint256(deliveryTip_)
