/**
 * @notice Shared utilities between Synapse System Contracts: Origin, Destination, etc.
 */
abstract contract SystemContract is DomainContext, Versioned, OwnableUpgradeable, ISystemContract {
    // ═════════════════════════════════════════════════ CONSTANTS ═════════════════════════════════════════════════════

    // domain of the Synapse Chain
    // For MVP this is Optimism chainId
    // TODO: replace the placeholder with actual value
    uint32 public constant SYNAPSE_DOMAIN = 10;

    uint256 internal constant ORIGIN = 1 << uint8(SystemEntity.Origin);
    uint256 internal constant DESTINATION = 1 << uint8(SystemEntity.Destination);
    uint256 internal constant AGENT_MANAGER = 1 << uint8(SystemEntity.AgentManager);

    // TODO: reevaluate optimistic period for staking/unstaking bonds
    uint32 internal constant BONDING_OPTIMISTIC_PERIOD = 1 days;

    // ══════════════════════════════════════════════════ STORAGE ══════════════════════════════════════════════════════

    InterfaceSystemRouter public systemRouter;

    /// @dev gap for upgrade safety
    uint256[49] private __GAP; // solhint-disable-line var-name-mixedcase

    // ═════════════════════════════════════════════════ MODIFIERS ═════════════════════════════════════════════════════

    /**
     * @dev Modifier for functions that are supposed to be called only from
     * System Contracts on all chains (either local or remote).
     * Note: any function protected by this modifier should have first three params as:
     * - uint256 proofMaturity
     * - uint32 origin
     * - SystemEntity sender
     * Make sure to check domain/sender, if a function should be only called
     * from a given domain / by a given sender.
     * Make sure to check that a needed amount of time has passed since
     * root submission for the cross-chain calls.
     */
    modifier onlySystemRouter() {
        _assertSystemRouter();
        _;
    }

    /**
     * @dev Modifier for functions that are supposed to be called only from
     * System Contracts on Synapse chain.
     * Note: has to be used alongside with `onlySystemRouter`
     * See `onlySystemRouter` for details about the functions protected by such modifiers.
     */
    modifier onlySynapseChain(uint32 callOrigin) {
        _assertSynapseChain(callOrigin);
        _;
    }

    /**
     * @dev Modifier for functions that are supposed to be called only from
     * a set of System Contracts on any chain.
     * Note: has to be used alongside with `onlySystemRouter`
     * See `onlySystemRouter` for details about the functions protected by such modifiers.
     * Note: check constants section for existing mask constants
     * E.g. to restrict the set of callers to three allowed system callers:
     *  onlyCallers(MASK_0 | MASK_1 | MASK_2, systemCaller)
     */
    modifier onlyCallers(uint256 allowedMask, SystemEntity systemCaller) {
        _assertEntityAllowed(allowedMask, systemCaller);
        _;
    }

    // ════════════════════════════════════════════════ OWNER ONLY ═════════════════════════════════════════════════════

    // solhint-disable-next-line ordering
    function setSystemRouter(InterfaceSystemRouter systemRouter_) external onlyOwner {
        systemRouter = systemRouter_;
    }

    /**
     * @dev Should be impossible to renounce ownership;
     * we override OpenZeppelin OwnableUpgradeable's
     * implementation of renounceOwnership to make it a no-op
     */
    function renounceOwnership() public override onlyOwner {} //solhint-disable-line no-empty-blocks

    // ═══════════════════════════════════════════ SYSTEM CALL SHORTCUTS ═══════════════════════════════════════════════

    /// @dev Perform a System Call to a AgentManager on a given domain
    /// with the given optimistic period and data.
    function _callAgentManager(uint32 domain, uint32 optimisticPeriod, bytes memory payload) internal {
        systemRouter.systemCall({
            destination: domain,
            optimisticPeriod: optimisticPeriod,
            recipient: SystemEntity.AgentManager,
            payload: payload
        });
    }

    // ══════════════════════════════════════════════ INTERNAL VIEWS ═══════════════════════════════════════════════════

    function _onSynapseChain() internal view returns (bool) {
        return localDomain == SYNAPSE_DOMAIN;
    }

    function _assertSystemRouter() internal view {
        require(msg.sender == address(systemRouter), "!systemRouter");
    }

    function _assertEntityAllowed(uint256 allowedMask, SystemEntity caller) internal pure {
        require(_entityAllowed(allowedMask, caller), "!allowedCaller");
    }

    function _assertSynapseChain(uint32 domain) internal pure {
        require(domain == SYNAPSE_DOMAIN, "!synapseDomain");
    }

    /**
     * @notice Checks if a given entity is allowed to call a function using a systemMask
     * @param systemMask  a mask of allowed entities
     * @param entity  a system entity to check
     * @return true if entity is allowed to call a function
     *
     * @dev this function works by converting the enum value to a non-zero bit mask
     * we then use a bitwise AND operation to check if permission bits allow the entity
     * to perform this operation, more details can be found here:
     * https://en.wikipedia.org/wiki/Bitwise_operation#AND
     */
    function _entityAllowed(uint256 systemMask, SystemEntity entity) internal pure returns (bool) {
        return systemMask & _getSystemMask(entity) != 0;
    }

    /**
     * @notice Returns a mask for a given system entity
     * @param entity  System entity
     * @return a non-zero mask for a given system entity
     *
     * Converts an enum value into a non-zero bit mask used for a bitwise AND check
     * E.g. for Origin (0) returns 1, for Destination (1) returns 2
     */
    function _getSystemMask(SystemEntity entity) internal pure returns (uint256) {
        return 1 << uint8(entity);
    }
}

abstract contract AgentManager is SystemContract, AgentManagerEvents, IAgentManager {
    // ══════════════════════════════════════════════════ STORAGE ══════════════════════════════════════════════════════

    ISystemRegistry public origin;

    ISystemRegistry public destination;

    // agent => (bool isSlashed, address prover)
    mapping(address => SlashStatus) public slashStatus;

    /// @dev gap for upgrade safety
    uint256[47] private __GAP; // solhint-disable-line var-name-mixedcase

    // ════════════════════════════════════════════════ INITIALIZER ════════════════════════════════════════════════════

    // solhint-disable-next-line func-name-mixedcase
    function __AgentManager_init(ISystemRegistry origin_, ISystemRegistry destination_) internal onlyInitializing {
        origin = origin_;
        destination = destination_;
    }

    // ══════════════════════════════════════════════ SLASHING LOGIC ═══════════════════════════════════════════════════

    /// @inheritdoc IAgentManager
    // solhint-disable-next-line ordering
    function registrySlash(uint32 domain, address agent, address prover) external {
        // Check that Agent hasn't been already slashed and initiate the slashing
        _initiateSlashing(domain, agent, prover);
        // On all chains both Origin and Destination/Summit could slash agents
        if (msg.sender == address(origin)) {
            _notifySlashing(DESTINATION, domain, agent, prover);
        } else if (msg.sender == address(destination)) {
            _notifySlashing(ORIGIN, domain, agent, prover);
        } else {
            revert("Unauthorized caller");
        }
        // Call "after slash" hook
        _afterRegistrySlash(domain, agent, prover);
    }

    // ═══════════════════════════════════════════════════ VIEWS ═══════════════════════════════════════════════════════

    /// @inheritdoc IAgentManager
    function getAgent(uint256 index) external view returns (address agent, AgentStatus memory status) {
        agent = _getAgent(index);
        if (agent != address(0)) status = agentStatus(agent);
    }

    /// @inheritdoc IAgentManager
    function agentStatus(address agent) public view returns (AgentStatus memory status) {
        status = _agentStatus(agent);
        // If agent was proven to commit fraud, but their slashing wasn't completed,
        // return the Fraudulent flag instead
        if (slashStatus[agent].isSlashed && status.flag != AgentFlag.Slashed) {
            status.flag = AgentFlag.Fraudulent;
        }
    }

    // ══════════════════════════════════════════════ INTERNAL LOGIC ═══════════════════════════════════════════════════

    /// @dev Hook that is called after agent was slashed on one of the Registries,
    /// and the remaining Registries were notified.
    // solhint-disable-next-line no-empty-blocks
    function _afterRegistrySlash(uint32 domain, address agent, address prover) internal virtual {}

    /// @dev Checks and initiates the slashing of an agent.
    /// Should be called, after one of registries confirmed fraud committed by the agent.
    function _initiateSlashing(uint32 domain, address agent, address prover) internal {
        // Check that Agent hasn't been already slashed
        require(!slashStatus[agent].isSlashed, "Already slashed");
        // Check that agent is Active/Unstaking and that the domains match
        AgentStatus memory status = _agentStatus(agent);
        require(
            (status.flag == AgentFlag.Active || status.flag == AgentFlag.Unstaking) && status.domain == domain,
            "Slashing could not be initiated"
        );
        slashStatus[agent] = SlashStatus({isSlashed: true, prover: prover});
        emit StatusUpdated(AgentFlag.Fraudulent, domain, agent);
    }

    /// @dev Notifies a given set of local registries about the slashed agent.
    /// Set is defined by a bitmask, eg: DESTINATION | ORIGIN
    function _notifySlashing(uint256 registryMask, uint32 domain, address agent, address prover) internal {
        // Notify Destination, if requested
        if (registryMask & DESTINATION != 0) destination.managerSlash(domain, agent, prover);
        // Notify Origin, if requested
        if (registryMask & ORIGIN != 0) origin.managerSlash(domain, agent, prover);
    }

    // ══════════════════════════════════════════════ INTERNAL VIEWS ═══════════════════════════════════════════════════

    /// @dev Generates leaf to be saved in the Agent Merkle Tree
    function _agentLeaf(AgentFlag flag, uint32 domain, address agent) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked(flag, domain, agent));
    }

    /// @dev Returns the last known status for the agent from the Agent Merkle Tree.
    function _agentStatus(address agent) internal view virtual returns (AgentStatus memory);

    /// @dev Returns agent address for the given index. Returns zero for non existing indexes.
    function _getAgent(uint256 index) internal view virtual returns (address);
}

interface InterfaceBondingManager {
    /**
     * @notice Adds a new agent for the domain. This is either a fresh address (Inactive),
     * or an agent who used to be active on the same domain before (Resting).
     * @dev Inactive: `proof` should be the proof of inclusion of an empty leaf
     * having index following the last added agent in the tree.
     * @dev Resting: `proof` should be the proof of inclusion of the agent leaf
     * with Resting flag having index previously assigned to the agent.
     * @param domain    Domain where the Agent will be active
     * @param agent     Address of the Agent
     * @param proof     Merkle proof of the Inactive/Resting status for the agent
     */
    function addAgent(uint32 domain, address agent, bytes32[] memory proof) external;

    /**
     * @notice Initiates the unstaking of the agent bond. Agent signature is immediately no longer
     * considered valid on Synapse Chain, and will be invalid on other chains once the Light Manager
     * updates their agent merkle root on these chains.
     * @dev `proof` should be the proof of inclusion of the agent leaf
     * with Active flag having index previously assigned to the agent.
     * @param domain    Domain where the Agent is active
     * @param agent     Address of the Agent
     * @param proof     Merkle proof of the Active status for the agent
     */
    function initiateUnstaking(uint32 domain, address agent, bytes32[] memory proof) external;

    /**
     * @notice Completes the unstaking of the agent bond. Agent signature is no longer considered
     * valid on any of the chains.
     * @dev `proof` should be the proof of inclusion of the agent leaf
     * with Unstaking flag having index previously assigned to the agent.
     * @param domain    Domain where the Agent was active
     * @param agent     Address of the Agent
     * @param proof     Merkle proof of the unstaking status for the agent
     */
    function completeUnstaking(uint32 domain, address agent, bytes32[] memory proof) external;

    /**
     * @notice Completes the slashing of the agent bond. Agent signature is no longer considered
     * valid under the updated Agent Merkle Root.
     * @dev `proof` should be the proof of inclusion of the agent leaf
     * with Active/Unstaking flag having index previously assigned to the agent.
     * @param domain    Domain where the Agent was active
     * @param agent     Address of the Agent
     * @param proof     Merkle proof of the active/unstaking status for the agent
     */
    function completeSlashing(uint32 domain, address agent, bytes32[] memory proof) external;

    /**
     * @notice Remote AgentManager should call this function to indicate that the agent
     * has been proven to commit fraud in the SystemRegistry on the origin chain.
     * @dev This initiates the process of agent slashing. It could be immediately
     * completed by anyone calling completeSlashing() providing a correct merkle proof
     * for the OLD agent status.
     * @param domain    Domain where the slashed agent was active
     * @param agent     Address of the slashed Agent
     * @param prover    Address that initially provided fraud proof in SystemRegistry
     */
    function remoteRegistrySlash(
        uint256 proofMaturity,
        uint32 callOrigin,
        SystemEntity systemCaller,
        uint32 domain,
        address agent,
        address prover
    ) external;

    /**
     * @notice Withdraws locked base message tips from requested domain Origin to the recipient.
     * Issues a call to a local Origin contract, or sends a system message to the remote chain.
     * @dev Could only be called by the Summit contract.
     * @param recipient     Address to withdraw tips to
     * @param origin        Domain where tips need to be withdrawn
     * @param amount        Tips value to withdraw
     */
    function withdrawTips(address recipient, uint32 origin, uint256 amount) external;

    // ═══════════════════════════════════════════════════ VIEWS ═══════════════════════════════════════════════════════

    /**
     * @notice Returns a leaf representing the current status of agent in the Agent Merkle Tree.
     * @dev Will return an empty leaf, if agent is not added to the tree yet.
     * @param agent     Agent address
     * @return leaf     Agent leaf in the Agent Merkle Tree
     */
    function agentLeaf(address agent) external view returns (bytes32 leaf);

    /**
     * @notice Returns a total amount of leafs representing known agents.
     * @dev This includes active, unstaking, resting and slashed agents.
     * This also includes an empty leaf as the very first entry.
     */
    function leafsAmount() external view returns (uint256 amount);

    /**
     * @notice Returns a full list of leafs from the Agent Merkle Tree.
     * @dev This might consume a lot of gas, do not use this on-chain.
     */
    function allLeafs() external view returns (bytes32[] memory leafs);

    /**
     * @notice Returns a list of leafs from the Agent Merkle Tree
     * with indexes [indexFrom .. indexFrom + amount).
     * @dev This might consume a lot of gas, do not use this on-chain.
     * @dev Will return less than `amount` entries, if indexFrom + amount > leafsAmount
     */
    function getLeafs(uint256 indexFrom, uint256 amount) external view returns (bytes32[] memory leafs);

    /**
     * @notice Returns a proof of inclusion of the agent in the Agent Merkle Tree.
     * @dev Will return a proof for an empty leaf, if agent is not added to the tree yet.
     * This proof could be used by ANY next new agent that calls {addAgent}.
     * @dev This WILL consume a lot of gas, do not use this on-chain.
     * @dev The alternative way to create a proof is to fetch the full list of leafs using
     * either {allLeafs} or {getLeafs}, and create a merkle proof from that.
     * @param agent     Agent address
     * @return proof    Merkle proof for the agent
     */
    function getProof(address agent) external view returns (bytes32[] memory proof);
}

interface InterfaceLightManager {
    /**
     * @notice Updates agent status, using a proof against the latest known Agent Merkle Root.
     * @dev Will revert if the provided proof doesn't match the latest merkle root.
     * @param agent     Agent address
     * @param status    Structure specifying agent status: (flag, domain, index)
     * @param proof     Merkle proof of Active status for the agent
     */
    function updateAgentStatus(address agent, AgentStatus memory status, bytes32[] memory proof) external;

    /**
     * @notice Updates the root of Agent Merkle Tree that the Light Manager is tracking.
     * Could be only called by a local Destination contract, which is supposed to
     * verify the attested Agent Merkle Roots.
     * @param agentRoot     New Agent Merkle Root
     */
    function setAgentRoot(bytes32 agentRoot) external;

    /**
     * @notice Withdraws locked base message tips from local Origin to the recipient.
     * @dev Could only be remote-called by BondingManager contract on Synapse Chain.
     * @param recipient     Address to withdraw tips to
     * @param amount        Tips value to withdraw
     */
    function remoteWithdrawTips(
        uint256 proofMaturity,
        uint32 callOrigin,
        SystemEntity systemCaller,
        address recipient,
        uint256 amount
    ) external;
}

