            // solhint-disable-previous-line no-inline-assembly
            // store the length
            mstore(ptr, written)
            // new pointer is old + 0x20 + the footprint of the body
            mstore(0x40, add(add(ptr, footprint_), 0x20))
            ret := ptr
        }
    }
}

