        uint256 signaturesAmount = signatures.length / SIGNATURE_LENGTH;
        if (signaturesAmount == 0 || signaturesAmount * SIGNATURE_LENGTH != signatures.length) {
            revert ThresholdECDSA__IncorrectSignaturesLength(signatures.length);
        }
