library TypeCasts {
    using TypedMemView for bytes;
    using TypedMemView for bytes29;

    function coerceBytes32(string memory _s) internal pure returns (bytes32 _b) {
        _b = bytes(_s).ref(0).index(0, uint8(bytes(_s).length));
    }

    // treat it as a null-terminated string of max 32 bytes
    function coerceString(bytes32 _buf) internal pure returns (string memory _newStr) {
        uint8 _slen = 0;
        while (_slen < 32 && _buf[_slen] != 0) {
            _slen++;
        }

        // solhint-disable-next-line no-inline-assembly
        assembly {
            _newStr := mload(0x40)
            mstore(0x40, add(_newStr, 0x40)) // may end up with extra
            mstore(_newStr, _slen)
            mstore(add(_newStr, 0x20), _buf)
        }
    }

    // alignment preserving cast
    function addressToBytes32(address _addr) internal pure returns (bytes32) {
        return bytes32(uint256(uint160(_addr)));
    }

    // alignment preserving cast
    function bytes32ToAddress(bytes32 _buf) internal pure returns (address) {
        return address(uint160(uint256(_buf)));
    }
}

library Header {
    using TypedMemView for bytes;
    using TypedMemView for bytes29;

    uint16 internal constant HEADER_VERSION = 1;

    /**
     * @dev Header memory layout
     * [000 .. 002): version            uint16   2 bytes
     * [002 .. 006): originDomain       uint32   4 bytes
     * [006 .. 038): sender             bytes32 32 bytes
     * [038 .. 042): nonce              uint32   4 bytes
     * [042 .. 046): destinationDomain  uint32   4 bytes
     * [046 .. 078): recipient          bytes32 32 bytes
     * [078 .. 082): optimisticSeconds  uint32   4 bytes
     */

    uint256 internal constant OFFSET_ORIGIN = 2;
    uint256 internal constant OFFSET_SENDER = 6;
    uint256 internal constant OFFSET_NONCE = 38;
    uint256 internal constant OFFSET_DESTINATION = 42;
    uint256 internal constant OFFSET_RECIPIENT = 46;
    uint256 internal constant OFFSET_OPTIMISTIC_SECONDS = 78;

    modifier onlyHeader(bytes29 _view) {
        _view.assertType(Message.HEADER_TYPE);
        _;
    }

    function formatHeader(
        uint32 _originDomain,
        bytes32 _sender,
        uint32 _nonce,
        uint32 _destinationDomain,
        bytes32 _recipient,
        uint32 _optimisticSeconds
    ) internal pure returns (bytes memory) {
        return
            abi.encodePacked(
                HEADER_VERSION,
                _originDomain,
                _sender,
                _nonce,
                _destinationDomain,
                _recipient,
                _optimisticSeconds
            );
    }

    function headerView(bytes memory _header) internal pure returns (bytes29) {
        return _header.ref(Message.HEADER_TYPE);
    }

    function headerVersion(bytes29 _header) internal pure onlyHeader(_header) returns (uint16) {
        return uint16(_header.indexUint(0, 2));
    }

    /// @notice Returns header's origin field
    function origin(bytes29 _header) internal pure onlyHeader(_header) returns (uint32) {
        return uint32(_header.indexUint(OFFSET_ORIGIN, 4));
    }

    /// @notice Returns header's sender field
    function sender(bytes29 _header) internal pure onlyHeader(_header) returns (bytes32) {
        return _header.index(OFFSET_SENDER, 32);
    }

    /// @notice Returns header's nonce field
    function nonce(bytes29 _header) internal pure onlyHeader(_header) returns (uint32) {
        return uint32(_header.indexUint(OFFSET_NONCE, 4));
    }

    /// @notice Returns header's destination field
    function destination(bytes29 _header) internal pure onlyHeader(_header) returns (uint32) {
        return uint32(_header.indexUint(OFFSET_DESTINATION, 4));
    }

    /// @notice Returns header's recipient field as bytes32
    function recipient(bytes29 _header) internal pure onlyHeader(_header) returns (bytes32) {
        return _header.index(OFFSET_RECIPIENT, 32);
    }

    /// @notice Returns header's optimistic seconds field
    function optimisticSeconds(bytes29 _header) internal pure onlyHeader(_header) returns (uint32) {
        return uint32(_header.indexUint(OFFSET_OPTIMISTIC_SECONDS, 4));
    }

    /// @notice Returns header's recipient field as an address
    function recipientAddress(bytes29 _header) internal pure returns (address) {
        return TypeCasts.bytes32ToAddress(recipient(_header));
    }
}

library Message {
    using TypedMemView for bytes;
    using TypedMemView for bytes29;

    /**
     * @dev This is only updated if the whole message structure is changed,
     *      i.e. if a new part is added.
     *      If already existing part is changed, the message version does not get bumped.
     */
    uint16 internal constant MESSAGE_VERSION = 1;

    /// @dev Parts.Last is used only for marking the last element of the enum
    enum Parts {
        Version,
        Header,
        Tips,
        Body,
        Last
    }

    uint40 internal constant MESSAGE_TYPE = 1337;
    uint40 internal constant HEADER_TYPE = uint40(Parts.Header);
    uint40 internal constant TIPS_TYPE = uint40(Parts.Tips);
    uint40 internal constant BODY_TYPE = uint40(Parts.Body);

    modifier onlyMessage(bytes29 _view) {
        _view.assertType(MESSAGE_TYPE);
        _;
    }

    /**
     * @dev Message memory layout
     *      All offsets are stored for backwards compatibility
     * [000 .. 002): version            uint16  2 bytes
     * [002 .. 004): header offset = 8  uint16  2 bytes
     * [004 .. 006): tips offset (AAA)  uint16  2 bytes
     * [006 .. 008): body offset (BBB)  uint16  2 bytes
     * [008 .. AAA): header             bytes   ? bytes
     * [AAA .. BBB): tips               bytes   ? bytes
     * [BBB .. CCC): body               bytes   ? bytes
     */

    /// @dev How much bytes is used for storing the version, or a single offset value
    uint8 internal constant TWO_BYTES = 2;
    /// @dev This value reflects the header offset in the latest message version
    uint16 internal constant HEADER_OFFSET = TWO_BYTES * uint8(Parts.Last);

    /**
     * @notice Returns formatted (packed) message with provided fields
     * @param _header Formatted header
     * @param _messageBody Raw bytes of message body
     * @return Formatted message
     **/
    function formatMessage(
        bytes memory _header,
        bytes memory _tips,
        bytes memory _messageBody
    ) internal pure returns (bytes memory) {
        // Version + Offsets + Header + Tips are supposed to fit within 65535 bytes
        uint16 tipsOffset = HEADER_OFFSET + uint16(_header.length);
        uint16 bodyOffset = tipsOffset + uint16(_tips.length);
        return
            abi.encodePacked(
                MESSAGE_VERSION,
                HEADER_OFFSET,
                tipsOffset,
                bodyOffset,
                _header,
                _tips,
                _messageBody
            );
    }

    /**
     * @notice Returns leaf of formatted message with provided fields.
     * @param _header Formatted header
     * @param _messageBody Raw bytes of message body
     * @return Leaf (hash) of formatted message
     **/
    function messageHash(
        bytes memory _header,
        bytes memory _tips,
        bytes memory _messageBody
    ) internal pure returns (bytes32) {
        return keccak256(formatMessage(_header, _tips, _messageBody));
    }

    function messageView(bytes memory _message) internal pure returns (bytes29) {
        return _message.ref(MESSAGE_TYPE);
    }

    /// @notice Returns message's header field as bytes29 (refer to TypedMemView library for details on bytes29 type)
    function header(bytes29 _message) internal pure onlyMessage(_message) returns (bytes29) {
        return
            _between(
                _message,
                _loadOffset(_message, Parts.Header),
                _loadOffset(_message, Parts.Tips),
                HEADER_TYPE
            );
    }

    /// @notice Returns message's tips field as bytes29 (refer to TypedMemView library for details on bytes29 type)
    function tips(bytes29 _message) internal pure onlyMessage(_message) returns (bytes29) {
        return
            _between(
                _message,
                _loadOffset(_message, Parts.Tips),
                _loadOffset(_message, Parts.Body),
                TIPS_TYPE
            );
    }

    /// @notice Returns message's body field as bytes29 (refer to TypedMemView library for details on bytes29 type)
    function body(bytes29 _message) internal pure onlyMessage(_message) returns (bytes29) {
        return _between(_message, _loadOffset(_message, Parts.Body), _message.len(), BODY_TYPE);
    }

    /// @notice Returns leaf of the formatted message.
    function leaf(bytes29 _message) internal pure onlyMessage(_message) returns (bytes32) {
        // TODO: do we actually need this?
        return _message.keccak();
    }

    function _between(
        bytes29 _message,
        uint256 _from,
        uint256 _to,
        uint40 _newType
    ) private pure returns (bytes29) {
        return _message.slice(_from, _to - _from, _newType);
    }

    /// @notice Loads offset for a given part of the message
    function _loadOffset(bytes29 _message, Parts _part) private pure returns (uint256) {
        return _message.indexUint(uint256(_part) * TWO_BYTES, TWO_BYTES);
    }
}

library Tips {
    using TypedMemView for bytes;
    using TypedMemView for bytes29;

    uint16 internal constant TIPS_VERSION = 1;

    /**
     * @dev Tips memory layout
     * [000 .. 002): version            uint16	 2 bytes
     * [002 .. 014): updaterTip         uint96	12 bytes
     * [014 .. 026): relayerTip         uint96	12 bytes
     * [026 .. 038): proverTip          uint96	12 bytes
     * [038 .. 050): processorTip       uint96	12 bytes
     */

    uint256 internal constant OFFSET_UPDATER = 2;
    uint256 internal constant OFFSET_RELAYER = 14;
    uint256 internal constant OFFSET_PROVER = 26;
    uint256 internal constant OFFSET_PROCESSOR = 38;

    modifier onlyTips(bytes29 _view) {
        _view.assertType(Message.TIPS_TYPE);
        _;
    }

    /**
     * @notice Returns formatted (packed) tips with provided fields
     * @param _updaterTip Tip for the Updater
     * @param _relayerTip Tip for the Relayer
     * @param _proverTip Tip for the Prover
     * @param _processorTip Tip for the Processor
     * @return Formatted tips
     **/
    function formatTips(
        uint96 _updaterTip,
        uint96 _relayerTip,
        uint96 _proverTip,
        uint96 _processorTip
    ) internal pure returns (bytes memory) {
        return abi.encodePacked(TIPS_VERSION, _updaterTip, _relayerTip, _proverTip, _processorTip);
    }

    /**
     * @notice Returns formatted empty tips
     * @return Formatted tips
     **/
    function emptyTips() internal pure returns (bytes memory) {
        return formatTips(0, 0, 0, 0);
    }

    /// @notice Returns view for the formatted tips
    /// @dev Providing anything other than formatted tips will lead to unexpected behavior
    function tipsView(bytes memory _tips) internal pure returns (bytes29) {
        return _tips.ref(Message.TIPS_TYPE);
    }

    /// @notice Returns version of formatted tips
    function tipsVersion(bytes29 _tips) internal pure onlyTips(_tips) returns (uint16) {
        return uint16(_tips.indexUint(0, 2));
    }

    /// @notice Returns updaterTip field
    function updaterTip(bytes29 _tips) internal pure onlyTips(_tips) returns (uint96) {
        return uint32(_tips.indexUint(OFFSET_UPDATER, 12));
    }

    /// @notice Returns relayerTip field
    function relayerTip(bytes29 _tips) internal pure onlyTips(_tips) returns (uint96) {
        return uint32(_tips.indexUint(OFFSET_RELAYER, 12));
    }

    /// @notice Returns proverTip field
    function proverTip(bytes29 _tips) internal pure onlyTips(_tips) returns (uint96) {
        return uint32(_tips.indexUint(OFFSET_PROVER, 12));
    }

    /// @notice Returns processorTip field
    function processorTip(bytes29 _tips) internal pure onlyTips(_tips) returns (uint96) {
        return uint32(_tips.indexUint(OFFSET_PROCESSOR, 12));
    }

    function totalTips(bytes29 _tips) internal pure onlyTips(_tips) returns (uint96) {
        return updaterTip(_tips) + relayerTip(_tips) + proverTip(_tips) + processorTip(_tips);
    }
}

