abstract contract UpdaterStorage is Initializable, OwnableUpgradeable {
    // ============ Immutable Variables ============

    // Domain of chain on which the contract is deployed
    uint32 public immutable localDomain;

    // ============ Public Variables ============

    // Address of bonded Updater
    address public updater;

    ISystemMessenger public systemMessenger;

    // ============ Upgrade Gap ============

    // gap for upgrade safety
    uint256[48] private __GAP;

    // ============ Events ============

    /**
     * @notice Emitted when update is made on Home
     * or unconfirmed update root is submitted on Replica
     * @param homeDomain Domain of home contract
     * @param nonce Nonce of new merkle root
     * @param root New merkle root
     * @param signature Updater's signature on `homeDomain`, `nonce` and `root`
     */
    // TODO: emit abi encoded update instead?
    event Update(
        uint32 indexed homeDomain,
        uint32 indexed nonce,
        bytes32 indexed root,
        bytes signature
    );

    /**
     * @notice Emitted when Updater is rotated
     * @param oldUpdater The address of the old updater
     * @param newUpdater The address of the new updater
     */
    event NewUpdater(address oldUpdater, address newUpdater);

    // ============ Constructor ============

    constructor(uint32 _localDomain) {
        localDomain = _localDomain;
    }

    // ============ Initializer ============

    function __SynapseBase_initialize(address _updater) internal onlyInitializing {
        __Ownable_init();
        _setUpdater(_updater);
    }

    // ============ Modifiers ============

    /**
     * @dev Modifier for functions that are supposed to be called from
     * System Contracts on other chains.
     */
    modifier onlySystemMessenger() {
        _assertSystemMessenger();
        _;
    }

    // ============ Restricted Functions ============

    function setSystemMessenger(ISystemMessenger _systemMessenger) external onlyOwner {
        systemMessenger = _systemMessenger;
    }

    // ============ Internal Functions ============

    function _assertSystemMessenger() internal view {
        require(msg.sender == address(systemMessenger), "!systemMessenger");
    }

    /**
     * @notice Set the Updater
     * @param _newUpdater Address of the new Updater
     */
    function _setUpdater(address _newUpdater) internal {
        address _oldUpdater = updater;
        updater = _newUpdater;
        emit NewUpdater(_oldUpdater, _newUpdater);
    }

    /**
     * @dev should be impossible to renounce ownership;
     * we override OpenZeppelin OwnableUpgradeable's
     * implementation of renounceOwnership to make it a no-op
     */
    function renounceOwnership() public override onlyOwner {
        // do nothing
    }
}

library Attestation {
    using TypedMemView for bytes;
    using TypedMemView for bytes29;

    /**
     * @dev AttestationData memory layout
     * [000 .. 004): homeDomain     uint32   4 bytes
     * [004 .. 008): nonce          uint32   4 bytes
     * [008 .. 040): root           bytes32 32 bytes
     *
     *      Attestation memory layout
     * [000 .. 040): data           bytes   40 bytes (see above)
     * [040 .. END): signature      bytes   ?? bytes (64/65 bytes)
     */

    uint256 internal constant OFFSET_HOME_DOMAIN = 0;
    uint256 internal constant OFFSET_NONCE = 4;
    uint256 internal constant OFFSET_ROOT = 8;
    uint256 internal constant ATTESTATION_DATA_LENGTH = 40;
    uint256 internal constant OFFSET_SIGNATURE = ATTESTATION_DATA_LENGTH;

    /**
     * @notice Returns formatted Attestation with provided fields
     * @param _data         Attestation Data (see above)
     * @param _signature    Notary's signature on `_data`
     * @return Formatted attestation
     **/
    function formatAttestation(bytes memory _data, bytes memory _signature)
        internal
        pure
        returns (bytes memory)
    {
        return abi.encodePacked(_data, _signature);
    }

    /**
     * @notice Returns formatted Attestation Data with provided fields
     * @param _domain   Domain of Home's chain
     * @param _root     New merkle root
     * @param _nonce    Nonce of the merkle root
     * @return Formatted data
     **/
    function formatAttestationData(
        uint32 _domain,
        uint32 _nonce,
        bytes32 _root
    ) internal pure returns (bytes memory) {
        return abi.encodePacked(_domain, _nonce, _root);
    }

    /**
     * @notice Checks that message is an Attestation, by checking its length
     */
    function isAttestation(bytes29 _view) internal pure returns (bool) {
        // Should have non-zero length for signature. Signature validity is not checked.
        return _view.len() > ATTESTATION_DATA_LENGTH;
    }

    /**
     * @notice Returns domain of chain where the Home contract is deployed
     */
    function attestationDomain(bytes29 _view) internal pure returns (uint32) {
        return uint32(_view.indexUint(OFFSET_HOME_DOMAIN, 4));
    }

    /**
     * @notice Returns nonce of Home contract at the time, when `root` was the Merkle root.
     */
    function attestationNonce(bytes29 _view) internal pure returns (uint32) {
        return uint32(_view.indexUint(OFFSET_NONCE, 4));
    }

    /**
     * @notice Returns a historical Merkle root from the Home contract
     */
    function attestationRoot(bytes29 _view) internal pure returns (bytes32) {
        return _view.index(OFFSET_ROOT, 32);
    }

    /**
     * @notice Returns Attestation's Data, that is going to be signed by the Notary
     */
    function attestationData(bytes29 _view) internal pure returns (bytes29) {
        return _view.slice(OFFSET_HOME_DOMAIN, ATTESTATION_DATA_LENGTH, 0);
    }

    /**
     * @notice Returns Notary's signature on AttestationData
     */
    function attestationSignature(bytes29 _view) internal pure returns (bytes29) {
        return _view.slice(OFFSET_SIGNATURE, _view.len() - ATTESTATION_DATA_LENGTH, 0);
    }
}

library Auth {
    using TypedMemView for bytes;
    using TypedMemView for bytes29;

    /**
     * @notice Recovers signer from data and signature.
     * @param _data         Data that was signed
     * @param _signature    `_data` signed by `signer`
     * @return signer       Address that signed the data
     */
    function recoverSigner(bytes29 _data, bytes memory _signature)
        internal
        pure
        returns (address signer)
    {
        bytes32 digest = _data.keccak();
        digest = ECDSA.toEthSignedMessageHash(digest);
        signer = ECDSA.recover(digest, _signature);
    }
}

abstract contract AuthManager {
