    }

    function _isUpdater(uint32 _homeDomain, address _updater)
        internal
        view
        override
        returns (bool)
    {
        require(_homeDomain == localDomain, "Wrong domain");
        return _updater == updater;
    }

    function _isWatchtower(address) internal pure override returns (bool) {
        return false;
    }

