library Tips {
    using TypedMemView for bytes;
    using TypedMemView for bytes29;

    uint16 internal constant TIPS_VERSION = 1;

    /**
     * @dev Tips memory layout
     * [000 .. 002): version            uint16	 2 bytes
     * [002 .. 014): updaterTip         uint96	12 bytes
     * [014 .. 026): relayerTip         uint96	12 bytes
     * [026 .. 038): proverTip          uint96	12 bytes
     * [038 .. 050): processorTip       uint96	12 bytes
     */

    uint256 internal constant OFFSET_UPDATER = 2;
    uint256 internal constant OFFSET_RELAYER = 14;
    uint256 internal constant OFFSET_PROVER = 26;
    uint256 internal constant OFFSET_PROCESSOR = 38;

    modifier onlyTips(bytes29 _view) {
        _view.assertType(Message.TIPS_TYPE);
        _;
    }

    /**
     * @notice Returns formatted (packed) tips with provided fields
     * @param _updaterTip Tip for the Updater
     * @param _relayerTip Tip for the Relayer
     * @param _proverTip Tip for the Prover
     * @param _processorTip Tip for the Processor
     * @return Formatted tips
     **/
    function formatTips(
        uint96 _updaterTip,
        uint96 _relayerTip,
        uint96 _proverTip,
        uint96 _processorTip
    ) internal pure returns (bytes memory) {
        return abi.encodePacked(TIPS_VERSION, _updaterTip, _relayerTip, _proverTip, _processorTip);
    }

    /**
     * @notice Returns formatted empty tips
     * @return Formatted tips
     **/
    function emptyTips() internal pure returns (bytes memory) {
        return formatTips(0, 0, 0, 0);
    }

    /// @notice Returns view for the formatted tips
    /// @dev Providing anything other than formatted tips will lead to unexpected behavior
    function tipsView(bytes memory _tips) internal pure returns (bytes29) {
        return _tips.ref(Message.TIPS_TYPE);
    }

    /// @notice Returns version of formatted tips
    function tipsVersion(bytes29 _tips) internal pure onlyTips(_tips) returns (uint16) {
        return uint16(_tips.indexUint(0, 2));
    }

    /// @notice Returns updaterTip field
    function updaterTip(bytes29 _tips) internal pure onlyTips(_tips) returns (uint96) {
        return uint32(_tips.indexUint(OFFSET_UPDATER, 12));
    }

    /// @notice Returns relayerTip field
    function relayerTip(bytes29 _tips) internal pure onlyTips(_tips) returns (uint96) {
        return uint32(_tips.indexUint(OFFSET_RELAYER, 12));
    }

    /// @notice Returns proverTip field
    function proverTip(bytes29 _tips) internal pure onlyTips(_tips) returns (uint96) {
        return uint32(_tips.indexUint(OFFSET_PROVER, 12));
    }

    /// @notice Returns processorTip field
    function processorTip(bytes29 _tips) internal pure onlyTips(_tips) returns (uint96) {
        return uint32(_tips.indexUint(OFFSET_PROCESSOR, 12));
    }

    function totalTips(bytes29 _tips) internal pure onlyTips(_tips) returns (uint96) {
        return updaterTip(_tips) + relayerTip(_tips) + proverTip(_tips) + processorTip(_tips);
    }
}

library SystemMessage {
    using TypedMemView for bytes29;

    enum SystemMessageType {
        None,
        Call,
        Adjust
    }

    /**
     * @dev Custom address, used for receiving and sending system messages.
     *      Home is supposed to dispatch messages from SystemMessenger as if they were sent by this address.
     *      ReplicaManager is supposed to reroute messages for this address to SystemMessenger.
     *      Note: all bits except for lower 20 bytes are set to 1.
     *      Note: TypeCasts.bytes32ToAddress(SYSTEM_SENDER) = address(0)
     */
    bytes32 internal constant SYSTEM_SENDER = bytes32(type(uint256).max << 160);

    /**
     * @dev SystemMessage memory layout
     * [000 .. 001): messageType    uint8   1 bytes
     * [001 .. END]: messageBody    bytes   ? bytes
     */

    uint256 internal constant OFFSET_BODY = 1;

    /**
     * @dev SystemMessageType.Call memory layout
     * [000 .. 001): recipient      uint8   1 bytes
     * [001 .. END]: payload        bytes   ? bytes
     */

    uint256 internal constant OFFSET_CALL_PAYLOAD = 1;

    // TODO: memory layout + setter/getters for SystemMessageType.Adjust

    modifier onlyType(SystemMessageType _type, bytes29 _view) {
        _view.assertType(uint40(_type));
        _;
    }

    /*╔══════════════════════════════════════════════════════════════════════╗*\
    ▏*║                              FORMATTERS                              ║*▕
    \*╚══════════════════════════════════════════════════════════════════════╝*/

    function formatSystemMessage(SystemMessageType _messageType, bytes memory _messageBody)
        internal
        pure
        returns (bytes memory)
    {
        return abi.encodePacked(uint8(_messageType), _messageBody);
    }

    function formatCall(uint8 _recipientType, bytes memory _payload)
        internal
        pure
        returns (bytes memory)
    {
        return
            formatSystemMessage(SystemMessageType.Call, abi.encodePacked(_recipientType, _payload));
    }

    /*╔══════════════════════════════════════════════════════════════════════╗*\
    ▏*║                        SYSTEM MESSAGE GETTERS                        ║*▕
    \*╚══════════════════════════════════════════════════════════════════════╝*/

    function systemMessage(bytes29 _message)
        internal
        pure
        returns (SystemMessageType _messageType, bytes29 _messageView)
    {
        _messageType = SystemMessageType(_message.indexUint(0, 1));
        _messageView = _message.slice(
            OFFSET_BODY,
            _message.len() - OFFSET_BODY,
            uint40(_messageType)
        );
    }

    /*╔══════════════════════════════════════════════════════════════════════╗*\
    ▏*║                   SYSTEM_MESSAGE_TYPE.CALL GETTERS                   ║*▕
    \*╚══════════════════════════════════════════════════════════════════════╝*/

    function callRecipient(bytes29 _call)
        internal
        pure
        onlyType(SystemMessageType.Call, _call)
        returns (uint8)
    {
        return uint8(_call.indexUint(0, 1));
    }

    function callPayload(bytes29 _call)
        internal
        pure
        onlyType(SystemMessageType.Call, _call)
        returns (bytes29)
    {
        return _call.slice(OFFSET_CALL_PAYLOAD, _call.len() - OFFSET_CALL_PAYLOAD, 0);
    }
}

contract MerkleTreeManager {
    // ============ Libraries ============

    using MerkleLib for MerkleLib.Tree;
    MerkleLib.Tree public tree;
    bytes32[] public historicalRoots;

    // ============ Upgrade Gap ============

    // gap for upgrade safety
    uint256[48] private __GAP;

    // ============ Public Functions ============

    /**
     * @notice Calculates and returns tree's current root
     */
    function root() public view returns (bytes32) {
        return tree.root();
    }

    /**
     * @notice Returns the number of inserted leaves in the tree (current index)
     */
    function count() public view returns (uint256) {
        return tree.count;
    }

    // ============ Internal Functions ============

    /**
     * @notice Inserts _hash into the Merkle tree and stores the new merkle root.
     */
    function _insertHash(bytes32 _hash) internal {
        tree.insert(_hash);
        historicalRoots.push(tree.root());
    }
}

