// contracts/interfaces/IAdminV2.sol

interface IAdminV2 {
    event CancelDelayUpdated(uint256 oldCancelDelay, uint256 newCancelDelay);
    event FeeRateUpdated(uint256 oldFeeRate, uint256 newFeeRate);
    event FeesSwept(address token, address recipient, uint256 amount);

    /// @notice Allows the governor to set the cancel delay. The cancel delay is the time period after the transaction
    /// deadline during which a transaction can be permissionlessly cancelled if it hasn't been proven by any Relayer.
    function setCancelDelay(uint256 newCancelDelay) external;

    /// @notice Allows the governor to set the protocol fee rate. The protocol fee is taken from the origin
    /// amount and is only applied to completed and claimed transactions.
    /// @dev The protocol fee is abstracted away from the relayers; they always operate using the amounts after fees.
    /// The origin amount they see in the emitted log is what they get credited with.
    function setProtocolFeeRate(uint256 newFeeRate) external;

    /// @notice Allows the governor to withdraw the accumulated protocol fees from the contract.
    function sweepProtocolFees(address token, address recipient) external;
}

// contracts/interfaces/IAdminV2Errors.sol

interface IAdminV2Errors {
    error CancelDelayBelowMin();
    error FeeRateAboveMax();
}

// contracts/interfaces/IFastBridge.sol

interface IFastBridge {
    struct BridgeTransaction {
        uint32 originChainId;
        uint32 destChainId;
        address originSender; // user (origin)
        address destRecipient; // user (dest)
        address originToken;
        address destToken;
        uint256 originAmount; // amount in on origin bridge less originFeeAmount
        uint256 destAmount;
        uint256 originFeeAmount;
        bool sendChainGas;
        uint256 deadline; // user specified deadline for destination relay
        uint256 nonce;
    }

    struct BridgeProof {
        uint96 timestamp;
        address relayer;
    }

    // ============ Events ============

    event BridgeRequested(
        bytes32 indexed transactionId,
        address indexed sender,
        bytes request,
        uint32 destChainId,
        address originToken,
        address destToken,
        uint256 originAmount,
        uint256 destAmount,
        bool sendChainGas
    );
    event BridgeRelayed(
        bytes32 indexed transactionId,
        address indexed relayer,
        address indexed to,
        uint32 originChainId,
        address originToken,
        address destToken,
        uint256 originAmount,
        uint256 destAmount,
        uint256 chainGasAmount
    );
    event BridgeProofProvided(bytes32 indexed transactionId, address indexed relayer, bytes32 transactionHash);
    event BridgeProofDisputed(bytes32 indexed transactionId, address indexed relayer);
    event BridgeDepositClaimed(
        bytes32 indexed transactionId, address indexed relayer, address indexed to, address token, uint256 amount
    );
    event BridgeDepositRefunded(bytes32 indexed transactionId, address indexed to, address token, uint256 amount);

    // ============ Methods ============

    struct BridgeParams {
        uint32 dstChainId;
        address sender;
        address to;
        address originToken;
        address destToken;
        uint256 originAmount; // should include protocol fee (if any)
        uint256 destAmount; // should include relayer fee
        bool sendChainGas;
        uint256 deadline;
    }

    /// @notice Initiates bridge on origin chain to be relayed by off-chain relayer
    /// @param params The parameters required to bridge
    function bridge(BridgeParams memory params) external payable;

    /// @notice Relays destination side of bridge transaction by off-chain relayer
    /// @param request The encoded bridge transaction to relay on destination chain
    function relay(bytes memory request) external payable;

    /// @notice Provides proof on origin side that relayer provided funds on destination side of bridge transaction
    /// @param request The encoded bridge transaction to prove on origin chain
    /// @param destTxHash The destination tx hash proving bridge transaction was relayed
    function prove(bytes memory request, bytes32 destTxHash) external;

    /// @notice Completes bridge transaction on origin chain by claiming originally deposited capital
    /// @param request The encoded bridge transaction to claim on origin chain
    /// @param to The recipient address of the funds
    function claim(bytes memory request, address to) external;

    /// @notice Disputes an outstanding proof in case relayer provided dest chain tx is invalid
    /// @param transactionId The transaction id associated with the encoded bridge transaction to dispute
    function dispute(bytes32 transactionId) external;

    /// @notice Refunds an outstanding bridge transaction in case optimistic bridging failed
    /// @param request The encoded bridge transaction to refund
    function refund(bytes memory request) external;

    // ============ Views ============

    /// @notice Decodes bridge request into a bridge transaction
    /// @param request The bridge request to decode
    function getBridgeTransaction(bytes memory request) external view returns (BridgeTransaction memory);

    /// @notice Checks if the dispute period has passed so bridge deposit can be claimed
    /// @param transactionId The transaction id associated with the encoded bridge transaction to check
    /// @param relayer The address of the relayer attempting to claim
    function canClaim(bytes32 transactionId, address relayer) external view returns (bool);
}

// contracts/interfaces/IFastBridgeV2Errors.sol

interface IFastBridgeV2Errors {
    error AmountIncorrect();
    error ChainIncorrect();
    error ExclusivityParamsIncorrect();
    error MsgValueIncorrect();
    error SenderIncorrect();
    error StatusIncorrect();
    error TokenNotContract();
    error ZapDataLengthAboveMax();
    error ZapNativeNotSupported();
    error ZeroAddress();

    error RecipientIncorrectReturnValue();
    error RecipientNoReturnValue();

    error DeadlineExceeded();
    error DeadlineNotExceeded();
    error DeadlineTooShort();
    error DisputePeriodNotPassed();
    error DisputePeriodPassed();
    error ExclusivityPeriodNotPassed();

    error TransactionRelayed();
}

// contracts/interfaces/IMulticallTarget.sol

/// @notice Interface for a contract that supports multiple calls from the same caller. Inspired by MulticallV3:
/// https://github.com/mds1/multicall/blob/master/src/Multicall3.sol
interface IMulticallTarget {
    struct Result {
        bool success;
        bytes returnData;
    }

    /// @notice Executes multiple calls to this contract in a single transaction while preserving msg.sender.
    /// Return data from the calls is discarded.
    /// @dev This method is non-payable, so only calls with msg.value of 0 can be batched.
    /// If ignoreReverts is set to true, reverted calls will be skipped.
    /// Otherwise, the entire batch will revert with the original revert reason.
    /// @param data             List of ABI-encoded calldata for the calls to execute
    /// @param ignoreReverts    Whether to skip calls that revert
    function multicallNoResults(bytes[] calldata data, bool ignoreReverts) external;

    /// @notice Executes multiple calls to this contract in a single transaction while preserving msg.sender.
    /// Return data from each call is preserved.
    /// @dev This method is non-payable, so only calls with msg.value of 0 can be batched.
    /// If ignoreReverts is set to true, reverted calls will be skipped.
    /// Otherwise, the entire batch will revert with the original revert reason.
    /// @param data             List of ABI-encoded calldata for the calls to execute
    /// @param ignoreReverts    Whether to skip calls that revert
    /// @return results         List of results from the calls, each containing (success, returnData)
    function multicallWithResults(
        bytes[] calldata data,
        bool ignoreReverts
    )
        external
        returns (Result[] memory results);
}

// contracts/interfaces/IZapRecipient.sol

/// @notice Interface for contracts that can perform Zap operations. Such contracts could be used as Recipients
/// in a FastBridge transaction that includes a Zap operation. The Zap Data should include instructions on how
/// exactly the Zap operation should be executed, which would typically include the target address and calldata
/// to use. The exact implementation of the Zap Data encoding is up to the Recipient contract.
interface IZapRecipient {
    /// @notice Performs a Zap operation with the given token and amount according to the provided Zap data.
