    function bridge(BridgeParams memory params) external payable {
        bridgeV2({
            params: params,
            paramsV2: BridgeParamsV2({
                quoteRelayer: address(0),
                quoteExclusivitySeconds: 0,
                quoteId: bytes(""),
                zapNative: 0,
                zapData: bytes("")
            })
        });
    }

