// contracts/interfaces/ISynapseExecutionServiceV1.sol

interface ISynapseExecutionServiceV1 is IExecutionService {
    error SynapseExecutionService__GasOracleNotSet();
    error SynapseExecutionService__FeeAmountTooLow(uint256 actual, uint256 required);
    error SynapseExecutionService__OptionsVersionNotSupported(uint16 version);
    error SynapseExecutionService__ZeroAddress();

    /// @notice Allows the contract governor to set the address of the EOA account that will be used
    /// to execute transactions on the remote chains.
    function setExecutorEOA(address executorEOA_) external;

    /// @notice Allows the contract governor to set the address of the gas oracle.
    function setGasOracle(address gasOracle_) external;

    /// @notice Allows the contract governor to set the global markup that the Execution Service charges
    /// on top of the GasOracle's gas cost estimates.
    function setGlobalMarkup(uint256 globalMarkup_) external;

    /// @notice Address of the gas oracle used for estimating the gas cost of the transactions.
    function gasOracle() external view returns (address);

    /// @notice The markup that the Execution Service charges on top of the GasOracle's gas cost estimates.
    /// Zero markup means that the Execution Service charges the exact gas cost estimated by the GasOracle.
    /// The markup is denominated in Wei, 1e18 being 100%.
    function globalMarkup() external view returns (uint256);
}

// contracts/libs/Options.sol

/// @notice Struct to hold V1 of options data.
/// @dev Next versions have to use the fields from the previous version and add new fields at the end.
/// @param gasLimit The gas limit for the transaction.
/// @param gasAirdrop The amount of gas to airdrop.
struct OptionsV1 {
    uint256 gasLimit;
    uint256 gasAirdrop;
}

using OptionsLib for OptionsV1 global;

/// @title OptionsLib
/// @notice A library for encoding and decoding Interchain options related to interchain messages.
library OptionsLib {
    using VersionedPayloadLib for bytes;

    uint16 internal constant OPTIONS_V1 = 1;

    error OptionsLib__IncorrectVersion(uint16 version);

    /// @notice Decodes options (V1 or higher) from a bytes format back into an OptionsV1 struct.
    /// @param data         The options data in bytes format.
    function decodeOptionsV1(bytes memory data) internal view returns (OptionsV1 memory) {
        uint16 version = data.getVersionFromMemory();
        if (version < OPTIONS_V1) {
            revert OptionsLib__IncorrectVersion(version);
        }
        // Structs of the same version will always be decoded correctly.
        // Following versions will be decoded correctly if they have the same fields as the previous version,
        // and new fields at the end: abi.decode ignores the extra bytes in the decoded payload.
        return abi.decode(data.getPayloadFromMemory(), (OptionsV1));
    }

    /// @notice Encodes V1 options into a bytes format.
    /// @param options      The OptionsV1 to encode.
    function encodeOptionsV1(OptionsV1 memory options) internal pure returns (bytes memory) {
        return VersionedPayloadLib.encodeVersionedPayload(OPTIONS_V1, abi.encode(options));
    }
}

