// contracts/interfaces/IInterchainDB.sol

interface IInterchainDB {
    /// @notice Struct representing an entry from the local Interchain DataBase.
    /// @param writer       The address of the writer on the local chain
    /// @param dataHash     The hash of the data written on the local chain
    struct LocalEntry {
        address writer;
        bytes32 dataHash;
    }

    /// @notice Struct representing an entry from the remote Interchain DataBase verified by the Interchain Module
    /// @param verifiedAt   The block timestamp at which the entry was verified by the module
    /// @param entryValue   The value of the entry: writer + dataHash hashed together
    struct RemoteEntry {
        uint256 verifiedAt;
        bytes32 entryValue;
    }

    error InterchainDB__ConflictingEntries(bytes32 existingEntryValue, InterchainEntry newEntry);
    error InterchainDB__EntryDoesNotExist(uint256 dbNonce);
    error InterchainDB__IncorrectFeeAmount(uint256 actualFee, uint256 expectedFee);
    error InterchainDB__NoModulesSpecified();
    error InterchainDB__SameChainId();

    /// @notice Write data to the Interchain DataBase as a new entry.
    /// Note: there are no guarantees that this entry will be available for reading on any of the remote chains.
    /// Use `verifyEntry` to ensure that the entry is available for reading on the destination chain.
    /// @param dataHash     The hash of the data to be written to the Interchain DataBase as a new entry
    /// @return dbNonce     The database nonce of the written entry on this chain
    function writeEntry(bytes32 dataHash) external returns (uint256 dbNonce);

    /// @notice Request the given Interchain Modules to verify the already written entry on the destination chain.
    /// Note: every module has a separate fee paid in the native gas token of the source chain,
    /// and `msg.value` must be equal to the sum of all fees.
    /// Note: this method is permissionless, and anyone can request verification for any entry.
    /// @dev Will revert if the entry with the given nonce does not exist.
    /// @param destChainId   The chain id of the destination chain
    /// @param dbNonce       The database nonce of the written entry on this chain
    /// @param srcModules    The source chain addresses of the Interchain Modules to use for verification
    function requestVerification(uint256 destChainId, uint256 dbNonce, address[] memory srcModules) external payable;

    /// @notice Write data to the Interchain DataBase,
    /// and request the given Interchain Modules to verify it on the destination chain.
    /// Note: every module has a separate fee paid in the native gas token of the source chain,
    /// and `msg.value` must be equal to the sum of all fees.
    /// Note: additional verification for the same entry could be later done using `requestVerification`
    /// by providing the returned `dbNonce`.
    /// @dev Will revert if the empty array of modules is provided.
    /// @param destChainId  The chain id of the destination chain
    /// @param dataHash     The hash of the data to be written to the Interchain DataBase as a new entry
    /// @param srcModules   The source chain addresses of the Interchain Modules to use for verification
    /// @return dbNonce     The database nonce of the written entry on this chain
    function writeEntryWithVerification(
        uint256 destChainId,
        bytes32 dataHash,
        address[] memory srcModules
    )
        external
        payable
        returns (uint256 dbNonce);

    /// @notice Allows the Interchain Module to verify the entry coming from a remote source chain.
    /// @param entry        The Interchain Entry to confirm
    function verifyEntry(InterchainEntry memory entry) external;

    /// @notice Get the fee for writing data to the Interchain DataBase, and verifying it on the destination chain
    /// using the provided Interchain Modules.
    /// @dev Will revert if the empty array of modules is provided.
    /// @param destChainId  The chain id of the destination chain
    /// @param srcModules   The source chain addresses of the Interchain Modules to use for verification
    function getInterchainFee(uint256 destChainId, address[] memory srcModules) external view returns (uint256);

    /// @notice Get the Interchain Entry by the writer and the writer nonce.
    /// @dev Will revert if the entry with the given nonce does not exist.
    /// @param dbNonce      The database nonce of the written entry on this chain
    function getEntry(uint256 dbNonce) external view returns (InterchainEntry memory);

    /// @notice Get the nonce of the database.
    function getDBNonce() external view returns (uint256);

    /// @notice Read the data written on specific source chain by a specific writer,
    /// and verify it on the destination chain using the provided Interchain Module.
    /// Note: returned zero value indicates that the module has not verified the entry.
    /// @param entry        The Interchain Entry to read
    /// @param dstModule    The destination chain addresses of the Interchain Modules to use for verification
    /// @return moduleVerifiedAt   The block timestamp at which the entry was verified by the module,
    ///                             or zero if the module has not verified the entry.
    function readEntry(
        address dstModule,
        InterchainEntry memory entry
    )
        external
        view
        returns (uint256 moduleVerifiedAt);
}

// contracts/InterchainClientV1.sol

/**
 * @title InterchainClientV1
 * @dev Implements the operations of the Interchain Execution Layer.
 */
contract InterchainClientV1 is Ownable, IInterchainClientV1 {
    using OptionsLib for bytes;

    uint64 public clientNonce;
    address public interchainDB;
    address public executionFees;
    mapping(bytes32 => bool) public executedTransactions;

    // Chain ID => Bytes32 Address of src clients
    mapping(uint256 => bytes32) public linkedClients;

    constructor() Ownable(msg.sender) {}

    // @inheritdoc IInterchainClientV1
    function setExecutionFees(address executionFees_) public onlyOwner {
        executionFees = executionFees_;
    }

    // @inheritdoc IInterchainClientV1
    function setInterchainDB(address _interchainDB) public onlyOwner {
        interchainDB = _interchainDB;
    }

    // @inheritdoc IInterchainClientV1
    function setLinkedClient(uint256 chainId, bytes32 client) public onlyOwner {
        linkedClients[chainId] = client;
    }

    /**
     * @notice Emitted when an interchain transaction is sent.
     */
    event InterchainTransactionSent(
        bytes32 srcSender,
        uint256 srcChainId,
        bytes32 indexed dstReceiver,
        uint256 indexed dstChainId,
        bytes message,
        uint64 nonce,
        bytes options,
        bytes32 indexed transactionId,
        uint256 dbNonce
    );

    // @notice Emitted when an interchain transaction is executed.
    // TODO: Indexing
    event InterchainTransactionExecuted(
        bytes32 indexed srcSender,
        uint256 indexed srcChainId,
        bytes32 dstReceiver,
        uint256 dstChainId,
        bytes message,
        uint64 nonce,
        bytes options,
        bytes32 indexed transactionId,
        uint256 dbNonce
    );

    /**
     * @dev Represents an interchain transaction.
     */
    struct InterchainTransaction {
        bytes32 srcSender;
        uint256 srcChainId;
        bytes32 dstReceiver;
        uint256 dstChainId;
        bytes message;
        uint64 nonce;
        bytes options;
        bytes32 transactionId;
        uint256 dbNonce;
    }

    function _generateTransactionId(
        bytes32 srcSender,
        uint256 srcChainId,
        bytes32 dstReceiver,
        uint256 dstChainId,
        bytes memory message,
        uint64 nonce,
        bytes memory options
    )
    internal
    pure
    returns (bytes32)
    {
        return keccak256(abi.encode(srcSender, srcChainId, dstReceiver, dstChainId, message, nonce, options));
    }

    // TODO: Calculate Gas Pricing per module and charge fees
    // @inheritdoc IInterchainClientV1
    function interchainSend(
        uint256 dstChainId,
        bytes32 receiver,
        address srcExecutionService,
        bytes calldata message,
        bytes calldata options,
        address[] calldata srcModules
    )
    public
    payable
    {
        uint256 verificationFees = IInterchainDB(interchainDB).getInterchainFee(dstChainId, srcModules);
        // TODO: should check msg.value >= totalModuleFees
        uint256 executionFee = msg.value - verificationFees;

        InterchainTransaction memory icTx = InterchainTransaction({
            srcSender: TypeCasts.addressToBytes32(msg.sender),
            srcChainId: block.chainid,
            dstReceiver: receiver,
            dstChainId: dstChainId,
            message: message,
            nonce: clientNonce,
            options: options,
            transactionId: 0,
            dbNonce: 0
        });

        bytes32 transactionId = _generateTransactionId(
            icTx.srcSender, icTx.srcChainId, icTx.dstReceiver, icTx.dstChainId, icTx.message, icTx.nonce, icTx.options
        );
        icTx.transactionId = transactionId;

        icTx.dbNonce = IInterchainDB(interchainDB).writeEntryWithVerification{value: verificationFees}(
            icTx.dstChainId, icTx.transactionId, srcModules
        );
        IExecutionService(srcExecutionService).requestExecution({
            dstChainId: dstChainId,
            // TODO: this should be encodedTx.length
            txPayloadSize: message.length,
            transactionId: transactionId,
            executionFee: executionFee,
            options: options
        });
        IExecutionFees(executionFees).addExecutionFee{value: executionFee}(dstChainId, transactionId);
        emit InterchainTransactionSent(
            icTx.srcSender,
            icTx.srcChainId,
            icTx.dstReceiver,
            icTx.dstChainId,
            icTx.message,
            icTx.nonce,
            icTx.options,
            icTx.transactionId,
            icTx.dbNonce
        );
        // Increment nonce for next message
        clientNonce++;
    }

    // TODO: App Config Versioning
    // TODO: What if receiver is not a contract / doesn't conform to interface?
    /**
     * @dev Retrieves the application configuration for a given receiver application.
     * @param receiverApp The address of the receiver application.
     * @return requiredResponses The number of required responses from the receiving modules.
     * @return optimisticTimePeriod The time period within which responses are considered valid.
     * @return approvedDstModules An array of addresses of the approved destination modules.
     */
    function _getAppConfig(address receiverApp)
    internal
    view
    returns (uint256 requiredResponses, uint256 optimisticTimePeriod, address[] memory approvedDstModules)
    {
        requiredResponses = IInterchainApp(receiverApp).getRequiredResponses();
        optimisticTimePeriod = IInterchainApp(receiverApp).getOptimisticTimePeriod();
        approvedDstModules = IInterchainApp(receiverApp).getReceivingModules();
    }

    // @inheritdoc IInterchainClientV1
    function isExecutable(bytes calldata transaction) public view returns (bool) {
        InterchainTransaction memory icTx = abi.decode(transaction, (InterchainTransaction));
        require(executedTransactions[icTx.transactionId] == false, "Transaction already executed");
        // Construct expected entry based on icTransaction data
        InterchainEntry memory icEntry = InterchainEntry({
            srcChainId: icTx.srcChainId,
            dbNonce: icTx.dbNonce,
            srcWriter: linkedClients[icTx.srcChainId],
            dataHash: icTx.transactionId
        });

        bytes32 reconstructedID = _generateTransactionId(
            icTx.srcSender, icTx.srcChainId, icTx.dstReceiver, icTx.dstChainId, icTx.message, icTx.nonce, icTx.options
        );

        require(icTx.transactionId == reconstructedID, "Invalid transaction ID");

        (uint256 requiredResponses, uint256 optimisticTimePeriod, address[] memory approvedDstModules) =
                        _getAppConfig(TypeCasts.bytes32ToAddress(icTx.dstReceiver));

        uint256[] memory approvedResponses = _getApprovedResponses(approvedDstModules, icEntry);

        uint256 finalizedResponses = _getFinalizedResponsesCount(approvedResponses, optimisticTimePeriod);
        require(finalizedResponses >= requiredResponses, "Not enough valid responses to meet the threshold");
        return true;
    }
    /**
     * @dev Calculates the number of responses that are considered finalized within the optimistic time period.
     * @param approvedResponses An array of timestamps when each approved response was recorded.
     * @param optimisticTimePeriod The time period in seconds within which a response is considered valid.
     * @return finalizedResponses The count of responses that are finalized within the optimistic time period.
     */

    function _getFinalizedResponsesCount(
        uint256[] memory approvedResponses,
        uint256 optimisticTimePeriod
    )
    internal
    view
    returns (uint256)
    {
        uint256 finalizedResponses = 0;
        for (uint256 i = 0; i < approvedResponses.length; i++) {
            if (approvedResponses[i] + optimisticTimePeriod <= block.timestamp) {
                finalizedResponses++;
            }
        }
        return finalizedResponses;
    }
    /**
     * @dev Retrieves the responses from approved modules for a given InterchainEntry.
