pragma solidity =0.8.20 ^0.8.0 ^0.8.13 ^0.8.20;

// contracts/events/InterchainClientV1Events.sol

abstract contract InterchainClientV1Events {
    /// @notice Emitted when the `ExecutionFees` contract is set.
    /// @param executionFees    The contract that will collect the execution fees.
    event ExecutionFeesSet(address executionFees);

    /// @notice Emitted when the InterchainClientV1 deployment on a remote chain is linked.
    /// @param chainId   The chain ID of the remote chain.
    /// @param client    The address of the InterchainClientV1 deployment on the remote chain.
    event LinkedClientSet(uint64 chainId, bytes32 client);

    /// @notice Emitted when a new interchain transaction is sent through the InterchainClientV1.
    /// The Receiver on the destination chain will receive the specified message once the transaction is executed.
    /// @param transactionId    The unique identifier of the interchain transaction.
    /// @param dbNonce          The nonce of batch containing the transaction's DB entry.
    /// @param entryIndex       The index of the transaction's DB entry in the batch.
    /// @param dstChainId       The chain ID of the destination chain.
    /// @param srcSender        The sender of the transaction on the source chain.
    /// @param dstReceiver      The receiver of the transaction on the destination chain.
    /// @param verificationFee  The fee paid to verify the batch on the destination chain.
    /// @param executionFee     The fee paid to execute the transaction on the destination chain.
    /// @param options          The execution options for the transaction.
    /// @param message          The payload of the message being sent.
    event InterchainTransactionSent(
        bytes32 indexed transactionId,
        uint64 dbNonce,
        uint64 entryIndex,
        uint64 dstChainId,
        bytes32 indexed srcSender,
        bytes32 indexed dstReceiver,
        uint256 verificationFee,
        uint256 executionFee,
        bytes options,
        bytes message
    );

    /// @notice Emitted when an interchain transaction is received by the InterchainClientV1.
    /// The Receiver on the destination chain has just received the message sent from the source chain.
    /// @param transactionId    The unique identifier of the interchain transaction.
    /// @param dbNonce          The nonce of batch containing the transaction's DB entry.
    /// @param entryIndex       The index of the transaction's DB entry in the batch.
    /// @param srcChainId       The chain ID of the source chain.
    /// @param srcSender        The sender of the transaction on the source chain.
    /// @param dstReceiver      The receiver of the transaction on the destination chain.
    event InterchainTransactionReceived(
        bytes32 indexed transactionId,
        uint64 dbNonce,
        uint64 entryIndex,
        uint64 srcChainId,
        bytes32 indexed srcSender,
        bytes32 indexed dstReceiver
    );

    /// @notice Emitted when the proof of execution is written to InterchainDB. This allows the source chain
    /// to verify that the transaction was executed by a specific executor, if necessary.
    /// @param transactionId    The unique identifier of the interchain transaction.
    /// @param dbNonce          The nonce of batch containing the written proof's DB entry.
    /// @param entryIndex       The index of the written proof's DB entry in the batch.
    /// @param executor         The address of the executor that completed the transaction.
    event ExecutionProofWritten(
        bytes32 indexed transactionId, uint64 dbNonce, uint64 entryIndex, address indexed executor
    );
}

// contracts/interfaces/IExecutionFees.sol

interface IExecutionFees {
    error ExecutionFees__AlreadyRecorded(uint64 dstChainId, bytes32 transactionId, address executor);
    error ExecutionFees__ZeroAddress();
    error ExecutionFees__ZeroAmount();

    /// @notice Add the execution fee for a transaction. The attached value will be added to the
    /// rewards for the executor completing the transaction.
    /// Note: this could be used to store the execution fee for a new transaction, or to add more
    /// funds to the execution fee of an existing transaction. Therefore this function is payable,
    /// and does not implement any caller restrictions.
    /// @dev Will revert if the executor is already recorded for the transaction.
    /// @param dstChainId           The chain id of the destination chain.
    /// @param transactionId        The id of the transaction to add the execution fee to.
    function addExecutionFee(uint64 dstChainId, bytes32 transactionId) external payable;

    /// @notice Record the executor (who completed the transaction) for a transaction,
    /// and update the accumulated rewards for the executor.
    /// @dev Could only be called by the Recorder.
    /// @param dstChainId           The chain id of the destination chain.
    /// @param transactionId        The id of the transaction to record the executor for.
    /// @param executor             The address of the executor who completed the transaction.
    function recordExecutor(uint64 dstChainId, bytes32 transactionId, address executor) external;

    /// @notice Allows the executor to claim their unclaimed rewards.
    /// @dev Will revert if the executor has no unclaimed rewards.
    function claimExecutionFees(address executor) external;

    // ═══════════════════════════════════════════════════ VIEWS ═══════════════════════════════════════════════════════

    /// @notice Get the accumulated rewards for an executor.
    /// @param executor             The address of the executor to get the rewards for.
    function accumulatedRewards(address executor) external view returns (uint256 accumulated);

    /// @notice Get the unclaimed rewards for an executor.
    /// @param executor             The address of the executor to get the rewards for.
    function unclaimedRewards(address executor) external view returns (uint256 unclaimed);

    /// @notice Get the total execution fee for a transaction.
    /// @param dstChainId           The chain id of the destination chain.
    /// @param transactionId        The id of the transaction to get the execution fee for.
    function executionFee(uint64 dstChainId, bytes32 transactionId) external view returns (uint256 fee);

    /// @notice Get the address of the recorded executor for a transaction.
    /// @dev Will return address(0) if the executor is not recorded.
    /// @param dstChainId           The chain id of the destination chain.
    /// @param transactionId        The id of the transaction to get the recorded executor for.
    function recordedExecutor(uint64 dstChainId, bytes32 transactionId) external view returns (address executor);
}

// contracts/interfaces/IExecutionService.sol

interface IExecutionService {
    /// @notice Request the execution of an Interchain Transaction on a remote chain.
    /// Note: the off-chain actor needs to fetch the transaction payload from the InterchainClient
    /// event with the same transactionId, then execute the transaction on the remote chain:
    /// `dstInterchainClient.executeTransaction(transactionPayload)`
    /// Once the execution is confirmed on the source chain, the off-chain actor will be able
    /// to claim `executionFee` in the ExecutionFees contract.
    /// @dev Could only be called by `InterchainClient` contracts.
    /// Will revert if the execution fee is not big enough.
    /// @param dstChainId           The chain id of the destination chain.
    /// @param txPayloadSize        The size of the transaction payload to use for the execution.
    /// @param transactionId        The id of the transaction to execute.
    /// @param executionFee         The fee paid for the execution.
