type ICTxHeader is uint256;

struct InterchainTransaction {
    uint64 srcChainId;
    uint64 dstChainId;
    uint64 dbNonce;
    uint64 entryIndex;
    bytes32 srcSender;
    bytes32 dstReceiver;
    bytes options;
    bytes message;
}

