// contracts/apps/examples/ExampleAppV1.sol

contract ExampleAppV1 is ICAppV1 {
    event MessageReceived(uint64 srcChainId, bytes32 sender, uint64 dbNonce, uint64 entryIndex, bytes message);
    event MessageSent(uint64 dstChainId, uint64 dbNonce, uint64 entryIndex, bytes32 transactionId);

    constructor(address admin) ICAppV1(admin) {
        _grantRole(IC_GOVERNOR_ROLE, admin);
    }

    /// @notice Allows the Admin to withdraw the native asset from the contract.
    function withdraw() external onlyRole(DEFAULT_ADMIN_ROLE) {
        Address.sendValue(payable(msg.sender), address(this).balance);
    }

    /// @notice Sends a basic message to the destination chain.
    function sendMessage(
        uint64 dstChainId,
        uint256 gasLimit,
        uint256 gasAirdrop,
        bytes calldata message
    )
        external
        payable
    {
        InterchainTxDescriptor memory desc = _sendToLinkedApp({
            dstChainId: dstChainId,
            messageFee: msg.value,
            options: OptionsV1({gasLimit: gasLimit, gasAirdrop: gasAirdrop}),
            message: message
        });
        emit MessageSent(dstChainId, desc.dbNonce, desc.entryIndex, desc.transactionId);
    }

    /// @notice Returns the fee required to send a message using `sendMessage`.
    function getMessageFee(
        uint64 dstChainId,
        uint256 gasLimit,
        uint256 gasAirdrop,
        bytes memory message
    )
        external
        view
        returns (uint256)
    {
        return _getMessageFee(dstChainId, OptionsV1({gasLimit: gasLimit, gasAirdrop: gasAirdrop}), message.length);
    }

    /// @dev Internal logic for receiving messages. At this point the validity of the message is already checked.
    function _receiveMessage(
        uint64 srcChainId,
        bytes32 sender,
        uint64 dbNonce,
        uint64 entryIndex,
        bytes calldata message
    )
        internal
        override
    {
        emit MessageReceived(srcChainId, sender, dbNonce, entryIndex, message);
    }
}

